  public static int determineFloor(String input){
    int floor = 0;

    for (int i = 0; i < input.length(); i++){
      switch (input.substring(i, i + 1)){
        case "(":
          floor++;
          break;
        case ")":
          floor--;
          break;
      }//end switch statement
    }//end for loop

    return floor;
  }//end determineFloor method

  public static int inBasement(String input){
    int floor = 0;
    int i = 0;

    for (i = 0; i < input.length(); i++){
      if (input.substring(i, i + 1).equals("(")){
        floor++;
      } else{
        floor--;
      }//end if/else

      if (floor == -1){
        return i + 1;
      }
    }//end for loop

    return 0;
  }//end inBasement method

